struct GdvAxisClass {
  GtkContainerClass parent_class;

  gboolean (*get_point)       (GdvAxis            *axis,
                               gdouble             value,
                               gdouble            *pos_x,
                               gdouble            *pos_y);

  gboolean (*get_inner_dir)   (GdvAxis            *axis,
                               gdouble             value,
                               gdouble            *pos_x,
                               gdouble            *pos_y);

  gchar *(*make_tic_label_markup)       (GdvAxis            *axis,
                                         gdouble             value);
};
